import React, { Component } from 'react'
import Form from './Form'
import List from './List'

export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todo: []
    }
    this.handleAdd = this.handleAdd.bind(this)
    this.handleRemove = this.handleRemove.bind(this)
  }

  state = {
    todo: JSON.parse(localStorage.getItem("todo")) || []
  }

  // データ保存
  handleAdd(e) {
    e.preventDefault()
    // フォームから受け取ったデータをオブジェクトに挿入して、stateのtodo配列に追加
    this.state.todo.push({ title: e.target.title.value, description: e.target.description.value })
    // setStateを使ってstateを上書き
    this.setState({ todo: this.state.todo })
    // inputのvalueを空に
    e.target.title.value = ''
    e.target.description.value = ''
  }

  // データ削除
  handleRemove(i) {
    // todo配列からi番目から1つ目のデータを除外
    this.state.todo.splice(i, 1)
    // setStateでtodo配列を上書き
    this.setState({ todo: this.state.todo })
  }

  render() {
    return (
      <div className="siimple-box siimple--bg-dark">
        <h1 className="siimple-box-title siimple--color-white">TO DO LIST</h1>
        <Form handleAdd={this.handleAdd} />
        <div className="siimple-rule"></div>
        <List todos={this.state.todo} handleRemove={this.handleRemove} />
      </div>
    )
  }
}
