import React, { Component } from 'react';
import './ToDoListItem.css';

class ToDoListItem extends Component {
    render() {
        const {
            title,
            description
        } = this.props;

        removeTodo = (item, callBack) => {
            this.setState(
                {
                    todoList: this.state.todoList.filter(x => x !== item)
                },
                () => {
                    // localStorageにtodoList stateを保存
                    localStorage.setItem("todoList", JSON.stringify(this.state.todoList))
                    // callBack関数が引数に渡されていた場合に実行
                    callBack && callBack()
                }
            )
        }

        return (
            <div className="ToDoListItem">
                <div className="ToDoListItem-title">{title}</div>
                <div className="ToDoListItem-description">{description}</div>
                <span onClick={() => this.removeTodo(todo)}>Delete</span>
            </div>
        );
    }
}

export default ToDoListItem;
