import React from 'react'

let style = { maxWidth: '700px' }
let btn = { cursor: 'pointer' }

const List = (props) => (
    <ul>
        {
            props.todos.map((todo, i) => {
                return <li key={i} style={style}> <input id="todo-0" type="checkbox" defaultChecked={true} /> {this.state.todoList.map(todo => (
                    <Todo
                        key={todo.title}
                        title={todo.title}
                        description={todo.description}
                    />
                ))}<span style={btn} onClick={() => props.handleRemove(i)}>Delete</span></li>
            })
        }
    </ul >
)

export default List
